create table public.products (
  id uuid not null default gen_random_uuid (),
  name text not null,
  description text null,
  category text not null,
  price_range text not null,
  specifications jsonb null default '{}'::jsonb,
  image_src text null,
  link text null default '/kapcsolat'::text,
  is_active boolean null default true,
  sort_order integer null default 0,
  created_at timestamp with time zone null default now(),
  updated_at timestamp with time zone null default now(),
  constraint products_pkey primary key (id),
  constraint products_category_check check (
    (
      category = any (
        array[
          'ablakok'::text,
          'ajtok'::text,
          'redonyok'::text,
          'accessories'::text
        ]
      )
    )
  ),
  constraint products_price_range_check check (
    (
      price_range = any (
        array['budget'::text, 'mid'::text, 'premium'::text]
      )
    )
  )
) TABLESPACE pg_default;

create index IF not exists idx_products_category on public.products using btree (category) TABLESPACE pg_default;

create index IF not exists idx_products_active on public.products using btree (is_active) TABLESPACE pg_default;

create index IF not exists idx_products_sort on public.products using btree (sort_order) TABLESPACE pg_default;

create trigger update_products_updated_at BEFORE
update on products for EACH row
execute FUNCTION update_updated_at_column ();

INSERT INTO "public"."products" ("id", "name", "description", "category", "price_range", "specifications", "image_src", "link", "is_active", "sort_order", "created_at", "updated_at") VALUES ('06a1b2c3-d4e5-4ddd-eeee-ffffaaaabbbb', 'Koncept', 'A Koncept ablak hagyományos, szögletes formájú. Ötkamrás A-osztályú profilja 70 mm beépítési mélységű, horganyzott acélmerevítésekkel és EPDM tömítéssel rendelkezik:contentReference[oaicite:7]{index=7}; opcionálisan alumínium ALU‑clip burkolattal is kérhető:contentReference[oaicite:8]{index=8}.', 'ablakok', 'budget', '{"Uw":"0,9 W/m²K","design":"hagyományos, szögletes","biztonsag":"acélmerevítések","kamraszam":"5 kamrás","uvegezés":"2 vagy 3 rétegű","beepitesi_melyseg":"70 mm"}', '/images/koncept.jpg', '/termekeink/koncept', 'true', '8', '2025-08-31 00:00:00+00', '2025-08-31 00:00:00+00'), ('16a1b2c3-d4e5-4eee-ffff-aaaabbbbcccc', 'HST Emelő-Tolóajtó', 'A HST emelő-tolóajtókkal akár 13,9 m²-es üvegfelület hozható létre; a nagy méret ellenére az emelő-toló mechanika könnyű és kényelmes működtetést biztosít, mert a szárnyak önálló vezetősíneken futnak:contentReference[oaicite:9]{index=9}.', 'ajtok', 'premium', '{"max_meret":"13.9 m²","mechanika":"emelő-toló"}', '/images/hst-toloajto.jpg', '/termekeink/muanyag-premium-ajtok', 'true', '9', '2025-08-31 00:00:00+00', '2025-08-31 15:11:50.884517+00'), ('26a1b2c3-d4e5-4fff-aaaa-bbbbccccdddd', 'PSK Bukó-Tolóajtó', 'A PSK bukó‑toló ajtók nagy üvegfelületük révén sok természetes fényt juttatnak a helyiségbe. A nyitási mód helytakarékos és kényelmes kijárást biztosít; két típusa van: párhuzamos‑toló (PSK Atrium) és bukó‑toló (PSK, PSK‑Z) rendszer:contentReference[oaicite:2]{index=2}.', 'ajtok', 'mid', '{"fajtak":"Párhuzamos-toló (PSK Atrium), bukó-toló (PSK, PSK-Z)"}', '/images/psk-toloajto.jpg', '/termekeink/buko-toloajto', 'true', '10', '2025-08-31 00:00:00+00', '2025-08-31 00:00:00+00'), ('36a1b2c3-d4e5-4000-bbbb-ccccddddeeee', 'Slide Tolóajtó', 'A SLIDE rendszer innovatív zárószerkezete